INSERT INTO `product` VALUES (1,'Trò chơi dẫn bóng mê cung','Trò chơi dẫn bóng mê cung được làm từ chất liệu gỗ. Kích thước 30 x 30 x 6 cm. Đồ chơi này thích hợp dành cho trẻ em hoặc làm đồ chơi xả stress trong nhóm bạn.',333,299000,2,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/07/tro-choi-dan-bong-me-cung-4.jpg'),(2,'Hộp bút xoay thông minh','Hộp bút đa năng có mật khẩu - Sản phẩm sáng chế. Thương hiệu: OEM',333,179000,6,NULL,1,'https://salt.tikicdn.com/cache/750x750/ts/product/70/6c/04/91dc95f2f19b2e9617fad35d7016e88f.jpg.webp'),(3,'Hộp bút Kimetsu no Yaiba','Chất liệu vải, hình ảnh in 2 mặt túiSắc nét đẹp giặt vò thoải mái, mẫu giao ngẫu nhiên',333,36000,6,NULL,1,'https://salt.tikicdn.com/cache/750x750/ts/product/89/cf/66/715597aed081cbb69783c5b85df8a9b4.jpg.webp'),(4,'Hộp đựng bút hình mèo cho bé PKS','Hộp đựng bút hình mèo cho bé là một trong những người bạn thân thiết của chúng ta trong suốt quá trình học tập. Nó đã giúp ích rất nhiều trong việc bảo quản những chiếc bút và đồ dùng nhỏ xinh khác. Chất liệu da mềm, nhẹ, bền đẹp cùng với sắc bắt mắt thoải mái cho bạn lựa chọn.',333,40000,6,NULL,1,'https://salt.tikicdn.com/cache/750x750/ts/product/d7/de/55/ae424799ec9111455ff0b2eb15f66b1d.jpg.webp'),(5,'Khung ảnh để bàn Minimalism','Khung ảnh được thiết kế theo phong cách Minimalism, hay còn gọi là phong cách tối giản - một phong trào nghệ thuật nổi lên ở New York vào những năm đầu thập niên 1960. Phong cách tối giản được hiểu là đơn giản hóa trong việc thiết kế, loại bỏ những thiết kế rườm rà, những màu sắc không cần thiết, tập trung vào sự đơn giản và đơn sắc. Bởi vậy nên những sản phẩm mang phong cách này thường rất tinh tế trong kiểu dáng, thanh lịch trong màu sắc.',333,160000,5,NULL,1,'https://anh.quatructuyen.com/media/catalog/product/cache/1/image/480x480/9df78eab33525d08d6e5fb8d27136e95/k/h/khung_anh_5.jpg'),(6,'Cốc Sứ Hai Màu','Đặc biệt ở mẫu cốc sứ in logo cốc sứ 2 màu trắng này không chỉ đẹp trong kiểu dáng lịch thiệp, hiện đại mà còn chất lượng vô cùng tốt. ',32,119000,1,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2020/10/coc-su-2-mau-5.jpg'),(7,'Con Quay Vortecon','Con quay Vortecon được làm từ thép không gỉ. Có 2 loại hình trụ hoặc hình cầu. Bạn có thể chọn loại mình thích thông qua phần ghi chú khi đặt hàng. Hiệu ứng ảo giác mà nó tạo ra là vô cùng ấn tượng.',333,199000,2,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/07/con-quay-vortecon-3.jpg'),(8,'Trò Chơi Bắn Bóng Rổ','Trò chơi bắn bóng rổ có kích thước chiều cao 24,5 cm, chiều rộng 13 cm và chiều dài 26 cm. Được làm từ chất liệu kim loại kết hợp với nhựa, bóng bằng cao su.  Sản phẩm có thể gấp gọn',333,199000,2,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/05/tro-choi-ban-bong-ro-2.jpg'),(9,'Hộp Con Dấu Chữ Cái','Hộp con dấu chữ cái được làm từ chất liệu gỗ. Các con dấu được làm theo bảng chữ cái la tinh, bao gồm cả chữ hoa và chữ thường. Ngoài ra còn có các con dấu chữ số và một số ký tự đặc biệt. Kích thước của mỗi con dấu dài 3,8 cm, rộng 0,9 cm và cao 1,2 cm.',333,219000,2,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2018/11/hop-con-dau-chu-cai-2.jpg'),(10,'Con Lắc Dao Động Vĩnh Cửu','Con lắc dao động vĩnh cửu được làm từ chất liệu nhựa là chủ đạo. Con lắc dùng pin 2A. Điểm nổi bật là những quả bóng nhiều màu sắc bắt mắt. Kích thước 10,5 x 23 cm. Con lắc có khả năng dao động liên tục không bị tác động bởi ma sát.',333,185000,2,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2018/11/con-lac-dao-dong-vinh-cuu-1.jpg'),(11,'Mô Hình Siêu Anh Hùng Marvel','Mô hình siêu anh hùng Marvel dựa trên hình tượng các nhân vật nổi tiếng trong các bộ phim chuyển thể từ truyện tranh của hãng Marvel. Sản phẩm được làm từ chất liệu nhựa tổng hợp. Sản phẩm thích hợp làm quà tặng cho trẻ em, quà tặng tết thiếu nhi.',333,329000,2,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/05/tuong-nguoi-nhen-spiderman-1.jpg'),(12,'Con Lắc Ra Quyết Định','Con lắc ra quyết định có kích thước chiều cao 21 cm, đường kính đế 10 cm. Phần đế chia thành 6 ô với 6 kết quả khác nhau mà người chơi phải tuân theo. Đây là món đồ chơi xả stress hiệu quả. ',333,149000,2,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/07/con-lac-ra-quyet-dinh-4.jpg'),(13,'Trò Chơi Xếp Gỗ','Trò chơi xếp gỗ được làm từ chất liệu gỗ tự nhiên, rất an toàn với trẻ em. Đây là một trò chơi trí tuệ, thúc đẩy phát triển khả năng tư duy của trẻ.',333,69000,2,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/07/tro-choi-xep-go-5.jpg'),(14,'Trò Chơi Sudoku Gỗ','Trò chơi Sudoku được làm từ gỗ tự nhiên. Trong một bộ trò chơi có rất nhiều bài khác nhau để bạn giải mã.  Khi không dùng tới bạn có thể gấp hộp gỗ lại rất gọn gàng.',333,299000,2,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/07/tro-choi-sudoku-go-1.jpg'),(15,'Con Quay Mezmo Top','Con quay Mezmo top được làm từ chất liệu nhôm và được chế tác bằng công nghệ CNC hiện đại. Có 3 màu sắc để lựa chọn: màu trắng, màu vàng và màu xám',333,259000,2,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2020/10/con-quay-mezmotop-4.jpg'),(16,'Đồng Hồ Cổ Để Bàn','Đồng hồ cổ để bàn được là theo phong cách cổ điển với chất liệu kim loại được sơn màu vàng đồng nhuốm màu thời gian. Trông chiếc đồng hồ tưởng chừng rất cũ kỹ nhưng thực ra lại không phải như vậy. Thiết kế cổ điển cùng với màu sắc giả cổ đã tôn lên vẻ cổ kính cho chiếc đồng hồ này.',333,499000,3,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2018/11/dong-ho-co-de-ban-1.jpg'),(17,'Đồng hồ báo thức trái tim','chiếc đồng hồ báo thức là vật dụng không thể thiếu trong cuộc sống hàng ngày của cá nhân và của gia đình. Ngày nay, dù có rất nhiều thiết bị khác nhau có chức năng xem giờ như điện thoại, máy tính, nhưng chiếc đồng hồ báo thức vẫn có giá trị sử dụng riêng. Mặt của đồng hồ báo thức trái tim được làm từ chất liệu kính.',333,380000,3,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/05/dong-ho-bao-thuc-trai-tim-5.jpg'),(18,'Đồng hồ báo thức cổ điển','Đồng hồ báo thức được làm theo phong cách cổ điển, được làm bằng chất liệu kim loại sơn màu đen mờ. Khi nhìn vào bạn tưởng như chúng vô cùng cũ kỹ nhưng không phải như bạn đang suy nghĩ. Thiết kế mang đậm phong cách cổ điển từ màu sắc cho tới các họa tiết trang trí bên trong với phong cách Vintage.',333,250000,3,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/05/dong-ho-bao-thuc-co-dien-3.jpg'),(19,'Đồng hồ dán tường DIY Clock 360','Không giống như các mẫu đồng hồ treo tường thông thường khác. Đồng hồ dán tường Diy Clock 360 là một loại đồng hồ dán tường độc đáo. Có thiết kế phá cách, không theo khuôn mẫu thông thường. Chiếc đồng hồ này gồm có 2 bộ phần chính.',333,290000,3,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2018/08/dong-ho-dan-tuong-diy-clocl-1.jpg'),(20,'Đồng hồ xe đạp 2 mặt','Đồng hồ cổ để bàn được là theo phong cách cổ điển với chất liệu kim loại được sơn màu vàng đồng nhuốm màu thời gian. Trông chiếc đồng hồ tưởng chừng rất cũ kỹ nhưng thực ra lại không phải như vậy. Thiết kế cổ điển cùng với màu sắc giả cổ đã tôn lên vẻ cổ kính cho chiếc đồng hồ này.',333,250000,3,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/05/dong-ho-xe-dap-2-mat-1.jpg'),(21,'Đồng hồ báo thức đèn Led','Đồng hồ có nhiều chức năng: báo ngày giờ, báo thức, nhiệt độ, ghi nhớ ngày tháng, … Đặc biệt có kèm theo bút có thể viết lên những lời ghi chú hay thông điệp muốn nhắn gửi. Đây là món quà tặng ý nghĩa cho bạn gái, hoặc làm quà sinh nhật, quà tặng bạn bè, …',333,290000,3,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/05/dong-ho-bao-thuc-den-led-2.jpg'),(22,'Đồng hồ báo thức Happy birthday','Đồng hồ báo thức Happy birthday được làm bằng chất liệu kim loại sơn bóng với nhiều màu sắc khác nhau cho bạn lựa chọn. Bên trong mặt số gồm 4 kim: Kim giờ, kim phút, kim giây và kim báo thức. Mặt trong đồng hồ được trang trí với nhiều chi tiết ngỗ nghĩnh, in dòng chữ Happy birtrday cách điệu trông đẹp mắt.',333,290000,3,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/05/dong-ho-bao-thuc-happy-birthday-1.jpg'),(23,'Mô hình đồng hồ tháp Big ben','Được lấy cảm hứng từ chiếc đồng hồ tháp Big ben của thủ đô Luân Đôn nước Anh – một trong những địa điểm nổi tiếng nhất trên Thế giới. Sản phẩm này là mô hình thu nhỏ, mô phỏng lại chiếc đồng hồ tháp Big ben. Mô hình đồng hồ tháp Big ben có phần lõi được làm từ chất liệu thủy tinh trong suốt. ',333,199000,3,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/04/mo-hinh-dong-ho-thap-big-ben-1.jpg'),(24,'Đồng hồ báo thức hình thỏ','Được làm từ chất liệu nhựa tổng hợp. Có nhiều màu sắc khác nhau. Có chức năng xem giờ, báo thức, có đèn Led.',333,250000,3,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2020/02/dong-ho-bao-thuc-hinh-tho-1.jpg'),(25,'Đồng hồ xe đạp 2 mặt','Bạn đang băn khăn chưa biết chọn sản phẩm nào cho riêng mình hay làm quà tặng đúng không? Hãy cùng Shop quà tặng trực tuyến tìm hiểu về 1 sản phẩm vừa làm đồ trang trí, có chức năng xem giờ, mang phong cách cổ điển sang trọng và thích hơp với nhiều không gian. Sản phẩm chúng tôi muốn giới thiệu ngay sau đây đó chính là chiếc đồng hồ xe đạp.',333,199000,3,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2018/08/dong-ho-xe-dap-co-1.jpg'),(26,'Đèn ngủ Led 3D','Sau những giờ làm việc căng thẳng, mệt mỏi, ai cũng muốn được ở bên gia đình thân yêu, ấm cúng với giấc ngủ ngon trong căn phòng nhỏ xinh. Một chiếc đèn đáng yêu không thể thiếu trong gian phòng của bạn để dễ dàng đưa bạn chìm sâu vào giấc ngủ.',333,390000,4,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/05/den-ngu-led-3D-1.jpg'),(27,'Đèn ngủ gà con','Đèn ngủ gà con được chia thành 2 bộ phận chính: Phần đế được làm từ nhựa ABS cao cấp, với thiết kế cách điệu giống với vỏ quả trứng khi chú gà con đang chui ra. Thiết kế hình quả trứng mang đến cho chiếc đèn này khả năng cân bằng rất tốt. Giống như những chú lật đật.',333,250000,4,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/05/den-ngu-ga-con-3.jpg'),(28,'Đèn ngủ mặt trăng','Đèn ngủ mặt trăng là một chiếc đèn ngủ có thiết kế rất độc đáo và ấn tượng. Chiếc đèn này được mô phỏng giống như bề mặt trên mặt trăng với những điểm lồi lõm, sáng tối khác nhau. Khi bạn nhìn vào chiếc đèn bạn có cảm tượng như đang nhìn vào mặt trăng vậy.',333,199000,4,NULL,1,'https://shopquatructuyen.com/san-pham/den-ngu-mat-trang/'),(29,'Đèn ngủ lồng kính','Đèn ngủ lồng kính được cấu tạo từ 2 phần chính. Phần thứ nhất là phần chân đế. Phần này được chế tác từ gỗ tự nhiên, nổi những đường vân gỗ rất đẹp mắt. Phần đế có gắn với dây dẫn điện và bóng đèn Led. Phần thứ 2 làm bằng thủy tinh được làm dưới dạng hình trụ, một đầu kín',333,120000,4,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/04/den-ngu-led-long-kinh-2.jpg'),(30,'Đèn ngủ bốt điện thoại','Hình ảnh chiếc bốt điện thoại công cộng màu đỏ nổi bật ở thủ đô Luân Đôn của nước Anh chắc chắn không còn xa lạ đối với chúng ta. Thậm chí nó còn có thể được coi như một trong những biểu tượng của thủ đô sứ sở sương mù.',333,199000,4,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/04/den-ngu-bot-dien-thoai-1.jpg'),(31,'Đèn ngủ 3D','Đèn ngủ 3D có cấu tạo chia thành 2 phần. Phần đế được làm từ chất liệu gỗ với màu nâu vàng tự nhiên và những đường vân rất đẹp mắt. Phần đế này được gắn những bóng đèn Led. Phần thứ 2 là phần chụp đèn được làm từ chất liệu thủy tinh. Đây cũng chính là phần tạo nên nét đặc biệt của chiếc đèn ngủ này.',333,399000,4,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2019/04/den-ngu-3D-1.jpg'),(32,'Đèn chiếu sao phiên bản 2','Đèn chiếu sao phiên bản 2 được làm từ chất liệu nhựa chủ đạo. Đèn có cấu tạo hình cầu giúp cho diện tích chiếu tỏa rộng và xa hơn. Đèn chiếu sao có thiết kế chắc chắn và kích thước lớn hơn so với phiên bản trước. Phần đế của đèn chiếu sao có nhiều màu sắc khác nhau: màu xanh, màu hồng, màu tím, …',333,199000,4,NULL,1,'https://shopquatructuyen.com/wp-content/uploads/2018/11/den-chieu-sao-phien-ba